import React, { useState } from 'react';
import FoodResultsDashboard from './FoodResultsDashboard';

function Dashboard({ goal }) {
  const [morningFood, setMorningFood] = useState('');
  const [eveningFood, setEveningFood] = useState('');
  const [postEveningFood, setPostEveningFood] = useState('');
  const [nightFood, setNightFood] = useState('');
  const [results, setResults] = useState(null);

  // Nutrition values per unit:
  // egg: per egg
  // chapathi: per chapathi
  // rice: per gram
  // brown bread: per gram and per slice (assume 1 slice = 25g)
  const foodData = {
    'egg': { protein: 6, carbs: 0.6, fiber: 0, unit: 'piece' },
    'chapathi': { protein: 3, carbs: 18, fiber: 2, unit: 'piece' },
    'rice': { protein: 0.028, carbs: 0.28, fiber: 0.004, unit: 'gram' },
    'brown bread': { protein: 0.034, carbs: 0.13, fiber: 0.015, unit: 'gram' },
    'brown bread slice': { protein: 0.85, carbs: 3.25, fiber: 0.38, unit: 'slice' }, // 25g per slice
  };

  function parseFoodEntry(entry) {
    if (!entry) return { food: entry, protein: 0, carbs: 0, fiber: 0 };
    const parts = entry.split(' ');
    let name = parts[0];
    let qty = 1;
    let unit = '';

    // Multi-word food names
    if (foodData[entry]) {
      name = entry;
    } else if (foodData[parts.slice(0,2).join(' ')]) {
      name = parts.slice(0,2).join(' ');
      if (parts.length > 2) {
        // e.g. brown bread 2 slice
        const num = parseFloat(parts[2]);
        if (!isNaN(num)) qty = num;
        if (parts.length > 3) unit = parts[3];
        else unit = parts[2].replace(/[0-9]/g, '');
      }
    } else {
      // e.g. rice 200g
      if (parts.length > 1) {
        const num = parseFloat(parts[1]);
        if (!isNaN(num)) qty = num;
        unit = parts[1].replace(/[0-9]/g, '');
      }
    }

    // Default units
    if (foodData[name]) {
      unit = foodData[name].unit;
    }

    // Special handling for rice and bread
    if (name === 'rice' && unit.includes('g')) {
      // qty is already in grams
      const data = foodData['rice'];
      return {
        food: entry,
        protein: +(data.protein * qty).toFixed(2),
        carbs: +(data.carbs * qty).toFixed(2),
        fiber: +(data.fiber * qty).toFixed(2)
      };
    }
    if (name === 'brown bread' && unit.includes('slice')) {
      // Use slice nutrition
      const data = foodData['brown bread slice'];
      return {
        food: entry,
        protein: +(data.protein * qty).toFixed(2),
        carbs: +(data.carbs * qty).toFixed(2),
        fiber: +(data.fiber * qty).toFixed(2)
      };
    }
    // Default: per piece
    const data = foodData[name] || { protein: 0, carbs: 0, fiber: 0 };
    return {
      food: entry,
      protein: +(data.protein * qty).toFixed(2),
      carbs: +(data.carbs * qty).toFixed(2),
      fiber: +(data.fiber * qty).toFixed(2)
    };
  }

  const handleCalculate = () => {
    const resultArr = [
      { time: 'Morning', ...parseFoodEntry(morningFood) },
      { time: 'Evening', ...parseFoodEntry(eveningFood) },
      { time: 'Post Evening', ...parseFoodEntry(postEveningFood) },
      { time: 'Night', ...parseFoodEntry(nightFood) },
    ];
    setResults(resultArr);
  };

  return (
    <div>
      <h2>Your Dashboard</h2>
      <p>Goal: {goal || 'Not set'}</p>
      <div style={{marginTop: '20px'}}>
        <h3>Food</h3>
        <ul style={{ listStyleType: 'none', paddingLeft: 0 }}>
          <li>
            <strong>Morning:</strong>
            <input
              type="text"
              placeholder="Enter morning food"
              value={morningFood}
              onChange={e => setMorningFood(e.target.value)}
              style={{ marginLeft: '10px' }}
            />
            {morningFood && (
              <div style={{ color: '#555', marginTop: 4 }}>Entered: {morningFood}</div>
            )}
          </li>
          <li>
            <strong>Evening:</strong>
            <input
              type="text"
              placeholder="Enter evening food"
              value={eveningFood}
              onChange={e => setEveningFood(e.target.value)}
              style={{ marginLeft: '10px' }}
            />
            {eveningFood && (
              <div style={{ color: '#555', marginTop: 4 }}>Entered: {eveningFood}</div>
            )}
          </li>
          <li>
            <strong>Post Evening:</strong>
            <input
              type="text"
              placeholder="Enter post evening food"
              value={postEveningFood}
              onChange={e => setPostEveningFood(e.target.value)}
              style={{ marginLeft: '10px' }}
            />
            {postEveningFood && (
              <div style={{ color: '#555', marginTop: 4 }}>Entered: {postEveningFood}</div>
            )}
          </li>
          <li>
            <strong>Night:</strong>
            <input
              type="text"
              placeholder="Enter night food"
              value={nightFood}
              onChange={e => setNightFood(e.target.value)}
              style={{ marginLeft: '10px' }}
            />
            {nightFood && (
              <div style={{ color: '#555', marginTop: 4 }}>Entered: {nightFood}</div>
            )}
          </li>
        </ul>
        {(morningFood && eveningFood && postEveningFood && nightFood) && (
          <button style={{marginTop: '16px', padding: '8px 16px', fontSize: '16px'}} onClick={handleCalculate}>Calculate</button>
        )}
      </div>
      {results && <FoodResultsDashboard results={results} />}
    </div>
  );
}

export default Dashboard;
